import React from 'react';  
import axios from 'axios';  
import '../Formas/addFormas.css'  
import { Container, Col, Form, Row, FormGroup, Label, Input, Button } from 'reactstrap';  

class addFormas extends React.Component{  
constructor(props){  
super(props)  
this.state = {  
NomeForma:'',
TipoForma:[] 
}  
}   
addFormas=()=>{  
  axios.post('http://localhost:49548/api/Formas', {NomeForma:this.state.NomeForma})  
.then(json => {  
if(json.data==='OK'){  
  console.log(json.data);  
  alert("Forma gravada com sucesso ! ! !");  
//this.props.history.push('/listaFormas')  
}  
else{  
alert('Confira os dados ! ! !');  
debugger;  
//this.props.history.push('/listaFormas')  
}  
})  
}  

handleChange= (e)=> {  
this.setState({[e.target.name]:e.target.value});  
}  

render() {  
return (  
   <Container className="App">  
    <h4 className="PageHeading">Digite o nome da Forma</h4>  
    <Form className="form">  
      <Col>  
        <FormGroup row>  
          <Label for="NomeForma" sm={2}>NomeForma</Label>  
          <Col sm={10}>  
            <Input type="text" name="NomeForma" onChange={this.handleChange} value={this.state.NomeForma} placeholder="Digite o Nome" />  
          </Col>  
          <Col sm={10}>  
          <select value={this.state.TipoForma}  onChange={this.handleChange}>
            <option value="1">Triangulo</option>
            <option value="2">Quadrado</option>
          </select>
          </Col>
        </FormGroup>  
      </Col>  
      <Col>  
        <FormGroup row>  
          <Col sm={5}>  
          </Col>  
          <Col sm={1}>  
          <button type="button" onClick={this.addFormas} className="btn btn-success">Enviar</button>  
          </Col>  
          <Col sm={1}>  
            <Button color="danger">Cancel</Button>{' '}  
          </Col>  
          <Col sm={5}>  
          </Col>  
        </FormGroup>  
      </Col>  
    </Form>  
  </Container>  
);  
}  

}  

export default addFormas;
