import React from 'react';  
import axios from 'axios';  
import '../Formas/addFormas.css'  
import { Container, Col, Form, Row, FormGroup, Label, Input, Button } from 'reactstrap';  

class addFormas extends React.Component{  
constructor(props){  
super(props)  
this.state = {  
NomeForma:'',
TipoForma:'' ,
Cor:'',
TamanhoPixels:'',
DiretorioId:'',
Select1Options: [],
Select2Options: [{DiretorioId: 1, NomeDiretorio: "Diretório Quadrado"},{DiretorioId: 2, NomeDiretorio: "Diretório Triangulo"}]
}  


axios.get("http://localhost:49548/api/Formas").then(response => {   
  this.state.Select1Options.push(response.data.TipoForma);
});  

}   
addFormas=()=>{  
  axios.post('http://localhost:49548/api/Formas', {NomeForma:this.state.NomeForma})  
.then(json => {  
if(json.data==='OK'){  
  console.log(json.data);  
  alert("Forma gravada com sucesso ! ! !");  
//this.props.history.push('/listaFormas')  
}  
else{  
alert('Confira os dados ! ! !');  
debugger;  
//this.props.history.push('/listaFormas')  
}  
})  
}  

handleChange= (e)=> {  
this.setState({[e.target.name]:e.target.value});  
}  
AlterarSelect1= (e)=>{
  this.setState({TipoForma: e.target.value })
}
AlterarSelect2= (e)=>{
  this.setState({DiretorioId: e.target.value })
}

render() {  
return (  
   <Container className="App">  
    <h4 className="PageHeading">Digite o nome da Forma</h4>  
    <Form className="form">  
      <Col>  
        <FormGroup row>  
          <Label for="NomeForma" sm={2}>NomeForma</Label>  
          <Col sm={10}>  
            <Input type="text" name="NomeForma" onChange={this.handleChange} value={this.state.NomeForma} placeholder="Digite o Nome da forma" />  
          </Col>  
          <Col sm={10}>  
         <select value={this.state.DiretorioId} onChange={this.AlterarSelect2}>
         {  
              this.state.Select2Options.map((p, Index) => {  
              return <option value={p.DiretorioId}>{p.NomeDiretorio}</option>;  
              })  
        }
         </select>
          </Col>
          <Col sm={10}>  
         <select value={this.state.TipoForma} onChange={this.AlterarSelect1}>
         {  
              this.state.Select1Options.map((p) => {  
              return <option value="">{p}</option>;  
              })  
        }
         </select>
          </Col>
          <Col sm={10}>  
            <Input type="text" name="Cor" onChange={this.handleChange} value={this.state.Cor} placeholder="Digite a cor" />  
          </Col>  
          <Col sm={10}>  
            <Input type="text" name="TamanhoPixels" onChange={this.handleChange} value={this.state.TamanhoPixels} placeholder="Digite o Tamanho em Pixels" />  
          </Col>  
        </FormGroup>  
      </Col>  
      <Col>  
        <FormGroup row>  
          <Col sm={5}>  
          </Col>  
          <Col sm={1}>  
          <button type="button" onClick={this.addFormas} className="btn btn-success">Enviar</button>  
          </Col>  
          <Col sm={1}>  
            <Button color="danger">Cancel</Button>{' '}  
          </Col>  
          <Col sm={5}>  
          </Col>  
        </FormGroup>  
      </Col>  
    </Form>  
  </Container>  
);  
}  

}  

export default addFormas;
