import React from 'react';  
import axios from 'axios';  
import '../Formas/addDiretorio.css'  

import { Navbar, Nav, Form, FormControl, Button } from "react-bootstrap";


class addDiretorio extends React.Component{  
constructor(props){  
super(props)  
this.state = {  
  NomeDiretorio:'' 
}  
}   
addDiretorio=()=>{  
  axios.post('http://localhost:49548/api/diretorios', {NomeDiretorio:this.state.NomeDiretorio})  
.then(json => {  
 
if(json.data==='OK'){  
  console.log(json.data);  
  alert("DiretÃ³rio adicionado com sucesso ! ! !");  
}  
else{  
alert('Confira os dados e tente novamente ! ! !');  
debugger;  
}  
})  
}  

handleChange= (e)=> {  
this.setState({[e.target.name]:e.target.value});  
}  

render() {  
return (  
  
   <Container className="App">  
       <Navbar bg="light" expand="md">
      <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link href="#link">Link</Nav.Link>
        </Nav>       
      </Navbar.Collapse>
    </Navbar>
    <h4 className="PageHeading">Digite o nome do diretÃ³rio</h4>  
    <Form className="form">  
      <Col>  
        <FormGroup row>  
          <Label for="NomeDiretorio" sm={2}>NomeDiretorio</Label>  
          <Col sm={10}>  
            <Input type="text" name="NomeDiretorio" onChange={this.handleChange} value={this.state.Name} placeholder="Digite o nome do diretÃ³rio"/>  
          </Col>  
        </FormGroup>  
      </Col>  
      <Col>  
        <FormGroup row>  
          <Col sm={5}>  
          </Col>  
          <Col sm={1}>  
          <button type="button" onClick={this.addDiretorio} className="btn btn-success">Enviar</button>  
          </Col>  
          <Col sm={1}>  
            <Button color="danger">Cancel</Button>{' '}  
          </Col>  
          <Col sm={5}>  
          </Col>  
        </FormGroup>  
      </Col>  
    </Form>  
  </Container>  
);  
}  

}  

export default addDiretorio;
